import React from "react";

export function FormHandler(route, setForm, form) {

    // Load default form
    fetch(route).then(response => response.json().then(data => {
        setForm(data.form)
    })).catch((e) => { console.log("Error occured: ",e) })
}

export function UpdateFormOnChange(event, form, setForm) {
    const targetEvent = event.target;
    const { id, value } = targetEvent;
    let updatedForm = form;
    updatedForm[id].value = value;
    setForm(updatedForm);
    console.log(updatedForm);
}
