import React, { useEffect, useState } from "react";

function _LoginForm(props) {

    const [ loginForm, setLoginForm ] = useState([]);

    // Load default view for submodule
    useEffect( () => {
        fetch("/_users").then(response => response.json().then(data => {
            setLoginForm(data.form);
        })).catch((e) => { console.log("Error occured: ",e) })
    }, [])


    function AfterFormSubmit(event) {
        event.preventDefault()

        // Simple POST request with a JSON body using fetch
        console.log("This is login form");
        console.log(event);

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            dataType: 'json',
            body: JSON.stringify({event: {email: event.target[0].attributes[0].value}})
        };
        fetch('/_login', requestOptions)
            .then(response => response.json())
            .then(data => console.log(data));
    }

    return (<div>
        <form onSubmit={AfterFormSubmit}>
            {console.log(loginForm)}
            {loginForm.length > 0 ? loginForm.map(CreateInput) : ""}
        </form>
    </div>)
}

function CreateInput(props) {
    return <Input
        key={props.label}
        label={props.label}
        type={props.type}
    />
}

function Input(props) {
    return <div>
        <label>{props.label}</label>
        <input type={props.type} />
    </div>
}


export default _LoginForm;
