import React from "react";

export function LoadForm(route, setForm) {

    // Load default form
    fetch(route).then(response => response.json().then(data => {
        setForm(data.form)
    })).catch((e) => { console.log("Error occured: ",e) })
}

// Update form on every change
export function UpdateFormOnChange(event, form, setForm) {
    const targetEvent = event.target;
    const { id, value } = targetEvent;
    let updatedForm = form;
    updatedForm[id].value = value;
    setForm(updatedForm);
    console.log(updatedForm);
}


// Action after submit button is pressed
export async function AfterFormSubmit(event, request) {

    event.preventDefault()

    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        dataType: 'json',
        body: JSON.stringify(request)
    };
    const response = await fetch('/_login', requestOptions);

    return response.json();

}


