import React from "react";

export function FormHandler(route, setForm, form) {

    // Load default form
    fetch(route).then(response => response.json().then(data => {
        setForm(data.form)
    })).catch((e) => { console.log("Error occured: ",e) })
}

export function UpdateFormOnChange(event, form, setForm) {
    const targetEvent = event.target;
    const { id, value } = targetEvent;
    let updatedForm = form;
    updatedForm[id].value = value;
    setForm(updatedForm);
    console.log(updatedForm);
}

export function AfterFormSubmit(request) {

    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        dataType: 'json',
        body: JSON.stringify(request)
    };
    fetch('/_login', requestOptions)
        .then(response => response.json())
        .then(data => {
            console.log(data);
            return 1;
        });
    return 0;
}

