import React, { useEffect, useState } from "react";
import {LoadForm, UpdateFormOnChange, AfterFormSubmit} from "../forms/FormHandler";


function _LoginForm(props) {

    const [ loginForm, setLoginForm ] = useState([]);


    // Load default view for submodule
    useEffect(() => {LoadForm("/_login", setLoginForm)}, []);


    // Handle form after submit button is pressed
    async function AfterLoginFormSubmit(event) {
        const response = await AfterFormSubmit(event, loginForm).then(response => console.log(response));
    }


    // Each character input in form is recorded to form const
    function UpdateLoginFormOnChange(event) {
        UpdateFormOnChange(event, loginForm, setLoginForm);
    }


    // Render form
    return (<div>
        <form onSubmit={AfterLoginFormSubmit}>
            {console.log(loginForm)}
            {loginForm.length > 0 ? loginForm.map((data) => { return <Input
                key={data.id}
                id={data.id}
                label={data.label}
                type={data.type}
                field={data.field}
                update={UpdateLoginFormOnChange}
            />  }) : ""}
        </form>
    </div>)
}

// Render each field
function Input(props) {
    return <div>
        <label>{props.label}</label>
        <input id={props.id} type={props.type} onChange={props.update} name={props.label} />
    </div>
}


export default _LoginForm;
