import React, { useEffect, useState } from "react";
import FormHandler from "../forms/FormHandler";


function _LoginForm(props) {

    const [ loginForm, setLoginForm ] = useState([]);
    const [ form, setForm ] = useState({
        "email": "",
        "password": ""
    });

    // Load default view for submodule
    useEffect(() => {FormHandler("/_login", setLoginForm)}, []);

    // Handle form after submit button is pressed
    function AfterFormSubmit(event) {

        event.preventDefault()

        // Simple POST request with a JSON body using fetch
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            dataType: 'json',
            body: JSON.stringify(form)
        };
        fetch('/_login', requestOptions)
            .then(response => response.json())
            .then(data => console.log(data));
    }

    // Each character input in form is recorded to form const
    function UpdateFormOnChange(event) {
        const updatedValue = event.target.value;
        console.log(event);


    }

    // Render form
    return (<div>
        <form onSubmit={AfterFormSubmit}>
            {console.log(loginForm)}
            {loginForm.length > 0 ? loginForm.map((data) => { return <Input
                key={data.id}
                id={data.id}
                label={data.label}
                type={data.type}
                field={data.field}
                update={UpdateFormOnChange}
            />  }) : ""}
        </form>
    </div>)
}

// Render each field
function Input(props) {
    return <div>
        <label>{props.label}</label>
        <input id={props.id} type={props.type} onChange={props.update} name={props.label} />
    </div>
}


export default _LoginForm;
